import axios from "../axios";

export default {
    actions: {
        async  fetchCategory({ commit }) {
            try{
                const { data } = await axios.get('http://localhost:8085/api/categories')
                const category = {
                            models: data['hydra:member'],
                            totalItems: data['hydra:totalItems']
                        }
                commit('updateCategory', category)
            } catch (error) {
                console.error(error);
                throw error;
            }
        }
    },

    mutations: {
        updateCategory(state, data) {
            state.category = data
        }
    },

    state: {
        category: {
            models: [],
            totalItems: 0
        }
    },

    getters: {
        getCategories(state) {
            return state.category.models
        }
    },

}
