import { createRouter, createWebHistory } from 'vue-router'
import HomeView from '../pages/HomePage.vue'
import Category from "@/plugins/vuex/books/category";

const routes = [
  {
    path: '/',
    name: 'home',
    component: HomeView,Category,
    children: [
      {
        path: '/category/:id',
        name: 'Category',
        component: HomeView
      },
      {
        path: '/book-info/:id',
        name: 'book',
        component: () => import('../pages/book/BookInfoPage.vue')
      },
    ]
  },
  {
    path: '/login',
    name: 'Login',
    component: () => import('../pages/book/LoginPage.vue')
  },
  {
    path: '/register',
    name: 'Register',
    component: () => import('../pages/book/RegisterPage.vue')
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes
})

export default router
