import axios from "../axios";

export default {

    actions: {
        fetchBookInfo(context, id) {

            return new Promise((resolve, reject) => {
                axios
                    .get('http://localhost:8085/api/books/' + id)
                    .then((response) => {
                        console.log('book success')
                        console.log(response)

                        let booksInfo = {
                            models: response.data['hydra:member'],
                            totalItems: response.data['hydra:totalItems']
                        }

                        context.commit('updateBookInfo', booksInfo)
                        resolve()

                    })
                    .catch(() => {
                        console.log('user err')
                        reject()
                    })
            })
        }
    },

    mutations: {
        updateBookInfo(state, data) {
            state.booksInfo = data
        }
    },

    state: {
        booksInfo: {
            models: [],
            totalItems: 0
        }
    },

    getters: {
        getBookInfo(state) {
            return state.booksInfo.models
        }
    },
}
