import axios from "../axios";

export default {

    actions: {
        async fetchBook({ commit }, categoryId = null, page = 2) {

            try {
                const { data } = await axios.get('http://localhost:8085/api/books',{
                    params: {
                        category: categoryId,
                        page: page,
                    }
                });

                const books = {
                    models: data['hydra:member'],
                    totalItems: Math.ceil(data['hydra:totalItems'] / 2) ,
                };

                commit('updateBooks', books);

            } catch (error) {
                console.error(error);
                throw error;
            }
        },

    },

    mutations: {
        updateBooks(state, data) {
            state.books = data
            state.books.currentPage
        },
    },

    state: {
        books: {
            models: [],
            totalItems: 0,
            currentPage: 0
        }

    },

    getters: {
        getBooks(state) {
            return state.books.models
        },
        getPage(state){
            return state.books.totalItems
        }

    },
}
