import axios from "../axios";

export default {

    actions: {
        fetchBookInfo(ctx, id) {

            return new Promise((resolve, reject) => {
                axios
                    .get('http://localhost:8085/api/books/' + id)
                    .then((response) => {
                        console.log('book success')
                        console.log(response)

                        let booksInfo = {
                            id: response.data.id,
                            name: response.data.name,
                            text: response.data.text,
                        }

                        ctx.commit('updateBookInfo', booksInfo)
                        resolve()

                    })
                    .catch(() => {
                        console.log('user err')
                        reject()
                    })
            })
        }
    },

    mutations: {
        updateBookInfo(state, data) {
            state.booksInfo = data
        }
    },

    state: {
        booksInfo: {
            id: null,
            name: '',
            text: ''
        }
    },

    getters: {
        getBookInfo(state) {
            return state.booksInfo
        }
    },
}
