import axios from "../axios";

export default {

    actions: {
        async fetchBook({ commit }, categoryId = null, booksPage = null) {

            const pageUrl = booksPage !== null ? '?page=' + booksPage : '';
            const categoryUrl = categoryId !== null ? '&category=' + categoryId : '';

            try {
                const { data } = await axios.get('http://localhost:8085/api/books' + pageUrl + categoryUrl);

                const books = {
                    models: data['hydra:member'],
                    totalItems: data['hydra:totalItems'],
                };

                commit('updateBooks', books);

            } catch (error) {
                console.error(error);
                throw error;
            }
        }
    },

    mutations: {
        updateBooks(state, data) {
            state.books = data
        }
    },

    state: {
        books: {
            models: [],
            totalItems: 0
        }

    },

    getters: {
        getBooks(state) {
            return state.books.models
        }
    },
}
