import axios from "../axios";

export default {
    actions: {
        async fetchCategories({ commit }) {
            try {
                const { data } = await axios.get('http://localhost:8085/api/categories');

                const categories = {
                    models: data['hydra:member'],
                    totalItems: data['hydra:totalItems']
                };

                commit('updateCategories', categories);

            } catch (error) {
                console.error(error);
                throw error;
            }
        }
    },

    mutations: {
        updateCategories(state, data) {
            state.categories = data
        }
    },

    state: {
        categories: {
            models: [],
            totalItems: 0
        }
    },

    getters: {
        getCategories(state) {
            return state.categories.models
        }
    }

}
